import { createStore } from "vuex";
import getRandomInt from '@/helpers/getRandomInt'

export default createStore({
  state: {
    count: 1,
    isLoading: false,
  },
  getters: {},
  mutations: {
    increment(state) {
      state.count++;
    },
    incrementBy(state, val) {
      state.count += val;
    },
  },
  actions: {
    async incrementRandomInt() {
      const RandomInt = await getRandomInt()
    }
  },
  modules: {},
});
